existing_concepts = {}
for concept in concepts:
    existing_concepts[concept['conceptLabel']] = concept['concept'].replace('https://eusterm.wikibase.cloud/entity/','')

lexlangprops = {'eu': 'P8', 'es':'P93', 'en': 'P94'}
desclangprops = {'eu': 'P13', 'es':'P95', 'en': 'P96'}
schemeqid = 'Q9547' # Paularena

with open('paula/iturriak.csv', 'r', encoding="utf-8") as csvfile:
    csvrows = csv.DictReader(csvfile, delimiter="\t")
    iturriak = {}
    for row in csvrows:
        iturriak[row['Iturria']] = row['wbqid']

with open('paula/terms_batch_1.csv', 'r', encoding="utf-8") as csvfile:
    csvrows = csv.DictReader(csvfile, delimiter="\t")
    lastlabel = ""

    for row in csvrows:

        label = row['label'].strip()
        wbqid = existing_concepts[label]
        if label != lastlabel:
            clear = True # new item; if label repeats in first column, it is the same concept
            print(f"Will clear item {wbqid} for data for label {label}")
        else:
            clear = False
            print(f"Will add to {wbqid} data for label {label}")

        print(f"Will use wb item {wbqid} for {label}.")
        label_lang = "en"
        if row['Wikidata'].strip().startswith("Q"):
            wdqid = row['Wikidata'].strip()
        else:
            wdqid = None
        if len(row['Iturria']) > 1:
            source_item = iturriak[row['Iturria'].strip()]
        else:
            source_item = None
        desc = row['Definizioa'].strip()
        desc = re.sub(r'\.$', '', desc)
        desc_lang = "en"

        labels = [{'lang': label_lang, 'value': label}]
        descriptions = [] # [{'lang': desc_lang, 'value': desc}]
        statements = [{'prop_nr': 'P6', 'type': 'item', 'value':schemeqid}]
        if wdqid and clear == True:
            statements.append({'prop_nr': 'P1', 'type': 'externalid', 'value': wdqid})
        if len(desc) > 0 and source_item:
            statements += [{'prop_nr': lexlangprops[label_lang], 'action': 'force', 'type': 'string', 'value': label,
                       'references': [{'prop_nr': 'P17', 'type': 'item', 'value':source_item}]},
                      {'prop_nr': desclangprops[label_lang], 'action': 'force', 'type': 'string', 'value': desc,
                       'references': [{'prop_nr': 'P17', 'type': 'item', 'value': source_item}]}]



        wbqid = euswbi.itemwrite({'qid': wbqid, 'statements': statements, 'labels': labels, 'descriptions': descriptions}, clear=clear)
        lastlabel = label
        time.sleep(1)

